import Input from "../../../components/input";
import React, { useEffect, useState } from "react";
import {addRecipe, getOneRecipe, updateRecipe} from "../../../Repositories/RecipeRepository";
import { Editor } from '@tinymce/tinymce-react';
import { useParams } from "react-router-dom";

export default function EditRecipe() {
    let { id } = useParams();

    const [formData, setFormData] = useState({
        headline: '',
        img_url: null,
        time: '',
        ingredients: [''],
        text: ''
    });

    useEffect(() => {
        const fetchData = async () => {
            const recipeData = await getOneRecipe(id);
            if (recipeData) {
                const { preparationTime, ...rest } = recipeData[0];
                setFormData({
                    ...rest,
                    time: preparationTime
                });
            }
        };

        fetchData();
    }, [id]);

    const handleEditorChange = (content, editor) => {
        setFormData({
            ...formData,
            text: content
        });
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleFileChange = (e) => {
        setFormData({
            ...formData,
            img_url: e.target.files[0]
        });
    };

    const handleIngredientChange = (index, value) => {
        const newIngredients = [...formData.ingredients];
        newIngredients[index] = value;
        setFormData({
            ...formData,
            ingredients: newIngredients
        });
    };

    const handleAddIngredient = () => {
        setFormData({
            ...formData,
            ingredients: [...formData.ingredients, '']
        });
    };

    const handleRemoveIngredient = (index) => {
        const newIngredients = [...formData.ingredients];
        newIngredients.splice(index, 1);
        setFormData({
            ...formData,
            ingredients: newIngredients
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        const formDataToSend = new FormData();
        formDataToSend.append('headline', formData.headline);
        formDataToSend.append('time', formData.time);
        formDataToSend.append('img_url', formData.img_url);
        formDataToSend.append('text', formData.text);
        formDataToSend.append("ingredients", formData.ingredients)

        await updateRecipe(formDataToSend, id);
    };

    return (
        <div className={"formObal"}>
            <form onSubmit={handleSubmit} encType={"multipart/form-data"}>
                <h1 className={"text-3xl font-bold text-center underline"}>Upravit recept</h1>
                <Input type={"text"} name={"headline"} label={"Nadpis"} value={formData.headline} onChange={handleInputChange}/>
                <Input required={false} type={"file"} name={"img_url"} label={"Obrázek"} onChange={handleFileChange}/>
                <Input type={"number"} name={"time"} label={"Doba přípravy (v minutách)"} value={formData.time} onChange={handleInputChange}/>
                <label className={"mt-3"}>Text</label>
                <Editor
                    apiKey="ghl18na9rl52go0feuz69quc62fqth5q5oyx3ek9j4qvn2uh"
                    value={formData.text}
                    onEditorChange={handleEditorChange}
                />
                {formData.ingredients.map((ingredient, index) => (
                    <div key={index}>
                        <Input type={"text"} name={`ingredients[]`} label={`Ingredience ${index + 1}`} value={ingredient} onChange={(e) => handleIngredientChange(index, e.target.value)}/>
                        <button className={"bg-white text-black py-1 w-2/12 text-center rounded-2xl mt-4"} type="button" onClick={() => handleRemoveIngredient(index)}>Odebrat ingredienci</button>
                    </div>
                ))}
                <button className={"bg-white text-black py-1 w-2/12 text-center rounded-2xl mt-4"} type="button" onClick={handleAddIngredient}>Přidat ingredienci</button>
                <div className={"flex flex-col items-center"}>
                    <button className={"bg-white text-black font-bold py-2 w-2/12 text-center rounded-2xl"} type="submit">Upravit</button>
                </div>
            </form>
        </div>
    );
}
