import ImageWithBorder from "../../components/ImageWithBorder";
import Title from "../../components/Title";
import Text from "../../components/Text";
import FlexDiv from "../../components/FlexDiv";

export default function TopBanner({data, stats}) {
    return (
        <div className="top-banner flex">
            <div className={"flex flex-row items-start gap-10 lg:px-20 py-5"}>
                <ImageWithBorder className={"h-28 w-28"} logo={data.team_id.image_src}></ImageWithBorder>
                <div>
                    <div className={"flex flex-row items-center gap-3"}>
                        <Title className={"font-bold text-3xl"} text={data.first_name + " " + data.last_name + " #" + data.number}></Title>
                    </div>
                    <FlexDiv className={"gap-3 flex mt-5"}>
                        <Text className={"font-bold"} text={"Pozice:"}></Text>
                        <Text text={data.position}></Text>
                    </FlexDiv>
                    stats.map((stat) => (
                        <FlexDiv className={"gap-3 flex mt-1"}>
                            <Text className={"font-bold"} text={stat.name + ":"}></Text>
                            <Text text={stat.value}></Text>
                        </FlexDiv>
                    ))
                </div>
            </div>
        </div>
    );
}
