import Input from "../../../components/input";
import React, {useEffect, useState} from "react";
import {useParams} from "react-router-dom";
import {getOneUser, updateUser} from "../../../Repositories/UserRepository";
import {getOneRecipe} from "../../../Repositories/RecipeRepository";
import Select from "../../../components/select";

export function EditUser() {
    let { id } = useParams();

    const [formData, setFormData] = useState({
        email: '',
        password: null,
        access: null,
    });

    const options = [
        { value: 0, label: "Uživatel" },
        { value: 1, label: "Administrátor" }
    ];

    useEffect(() => {
        const fetchData = async () => {
            const recipeData = await getOneUser(id);
            if (recipeData) {
               setFormData(recipeData)
            }
        };

        fetchData();
    }, [id]);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        const formDataToSend = new FormData();
        formDataToSend.append('email', formData.email);
        formDataToSend.append('password', formData.password);
        formDataToSend.append('access', formData.access);

        await updateUser(formDataToSend, id);
    };

    return (
        <div className={"formObal"}>
            <form onSubmit={handleSubmit} encType={"multipart/form-data"}>
                <h1 className={"text-3xl font-bold text-center underline"}>Upravit uživatele</h1>
                <Input type={"text"} name={"email"} label={"Email"} value={formData.email}
                       onChange={handleInputChange}/>
                <Input type={"password"} name={"password"} label={"Heslo"}
                       onChange={handleInputChange}/>
                <Select name={"access"} label={"Práva"} value={formData.access} options={options} onChange={handleInputChange}></Select>
                <div className={"flex flex-col items-center"}>
                    <button className={"bg-white text-black font-bold py-2 w-2/12 mt-4 text-center rounded-2xl"} type="submit">Upravit</button>
                </div>
            </form>
        </div>
    );
}
