import React from "react";
import MatchPanel from "../../components/matchPanel";
import Match from "../../components/match";
import PanelBlock from "../../components/PanelBlock";

export default function ContentTeam({ type, data }) {
    console.log(data);

    const groupedPlayers = data.players.reduce((acc, player) => {
        if (!acc[player.position]) {
            acc[player.position] = [];
        }
        acc[player.position].push(player);
        return acc;
    }, {});

    return (
        <div className="lg:px-20 py-20">
            {type === "results" && (
                <div>
                    <MatchPanel headline="Výsledky">
                        <Match />
                    </MatchPanel>
                </div>
            )}
            {type === "program" && <p>Zde bude program zápasů.</p>}
            {type === "lineUp" && (
                <div>
                    <MatchPanel headline="Soupiska týmů">
                        {Object.entries(groupedPlayers).map(([position, players]) => (
                            <PanelBlock key={position} position={position} players={players} />
                        ))}
                    </MatchPanel>
                </div>
            )}
        </div>
    );
}
