import React, { useState, useEffect } from 'react';
import HeaderNavigation from "../headerNavigation";
import Input from "../../components/input";

export default function Home() {
    const [date, setDate] = useState(""); // Stav pro sledování hodnoty data

    // Nastavení dnešního data při načtení komponenty
    useEffect(() => {
        const today = new Date().toISOString().split("T")[0]; // Získání dnešního data ve formátu YYYY-MM-DD
        setDate(today); // Nastavení stavu
    }, []);

    const handleSpanClick = () => {
        const inputElement = document.querySelector(".inputDate");
        if (inputElement) {
            inputElement.showPicker(); // Otevře kalendář (moderní prohlížeče)
            inputElement.focus();
        }
    };

    return (
        <div id={"home"}>
            <HeaderNavigation />
            <div className="custom-date">
                <Input
                    className={"flex justify-end items-end w-fit inputDate"}
                    type={"date"}
                    value={date} // Připojení řízeného stavu
                    onChange={(e) => setDate(e.target.value)} // Aktualizace stavu při změně hodnoty
                />
                <span className="icon" onClick={handleSpanClick}>
                    <img src={"/images/calendar.svg"} alt={"calendar"} />
                </span>
            </div>
        </div>
    );
}
