import React, { useState, useEffect } from 'react';
import Cookies from 'js-cookie';

const CookiesBanner = () => {
    const [isVisible, setIsVisible] = useState(false);

    useEffect(() => {
        const cookieConsent = Cookies.get('cookieConsent');
        if (!cookieConsent) {
            setIsVisible(true);
        }
    }, []);

    const handleAccept = () => {
        Cookies.set('cookieConsent', 'true', { expires: 7 });
        setIsVisible(false);
    };

    return (
        isVisible && (
            <div style={styles.banner}>
                <p>We use cookies to improve your experience. By continuing, you agree to our use of cookies.</p>
                <button onClick={handleAccept} style={styles.button}>
                    Accept
                </button>
            </div>
        )
    );
};

const styles = {
    banner: {
        position: 'fixed',
        bottom: '0',
        left: '0',
        right: '0',
        backgroundColor: '#241F55',
        color: 'white',
        padding: '40px 10px',
        textAlign: 'center',
        zIndex: 1000,
    },
    button: {
        backgroundColor: '#4CAF50',
        color: 'white',
        border: 'none',
        padding: '10px 20px',
        marginTop: '20px',
        fontSize: '16px',
        cursor: 'pointer',
        marginLeft: '10px',
    }
};

export default CookiesBanner;
