class UserLocalStorage {
    static getUser() {
        const userString = localStorage.getItem("loginUser");
        return userString ? JSON.parse(userString) : null;
    }

    static getUserId() {
        const user = UserLocalStorage.getUser();
        return user ? user.token : null; // Pokud máš ID v tokenu nebo jiném poli, změň zde
    }

    static setUser(user) {
        // Ukládá celý objekt uživatele do localStorage
        localStorage.setItem("loginUser", user);
    }

    static getUserEmail() {
        const user = UserLocalStorage.getUser();
        return user ? user.email : null;
    }

    static getUsername() {
        const user = UserLocalStorage.getUser();
        return user ? user.username : null;
    }

    static getUserRole() {
        const user = UserLocalStorage.getUser();
        return user ? user.role : null;
    }

    static isUserLoggedIn() {
        return !!localStorage.getItem("loginUser");
    }

    static isAdmin() {
        const user = UserLocalStorage.getUser();
        return user && user.role === "Admin"; // Podle role přizpůsobit
    }

    static clearUser() {
        localStorage.removeItem("loginUser");
    }
}

export default UserLocalStorage;
