import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import getTable from "../../Repositories/LeagueRepository";
import LoadingSpinner from "../../components/LoadingSpinner";
import Breadcrumbs from "../../components/breadcrumbs";

export default function LeagueDetail() {
    let { id } = useParams();
    let [data, setData] = useState(null);
    let [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchData = async () => {
            setLoading(true);
            try {
                let leagueData = await getTable(id);
                setData(leagueData);
            } catch (error) {
                console.error("Chyba při načítání dat:", error);
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, [id]);

    if (loading) {
        return <LoadingSpinner><p>Načítám data...</p></LoadingSpinner>;
    }

    if (!data || !data.teams || data.teams.length === 0) {
        return <div className="errorPage">Žádná data pro aktuální sezónu.</div>;
    }

    return (
        <div>
        <Breadcrumbs />
        <div className="league-detail">
            <div className="league-detail-head">
                <h1 className="league-title">{data.league?.name} - {data.league?.association}</h1>
                <p>{data.league?.sport}</p>
            </div>
            <h2 className="table-title">Tabulka</h2>
            <table className="league-table">
                <thead>
                <tr>
                    <th>Tým</th>
                    <th>Body</th>
                </tr>
                </thead>
                <tbody>
                {data.teams.sort((a, b) => b.points - a.points).map(team => (
                    <tr key={team.id}>
                        <td>{team.name}</td>
                        <td>{team.points}</td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
        </div>
    );
}
