import React, {useEffect, useState} from "react";
import {useParams} from "react-router-dom";
import Breadcrumbs from "../../components/Breadcrumbs";
import getTeam from "../../Repositories/TeamRepository";
import TopBanner from "../League/TopBanner";
import MatchControlPanel from "../../components/matchDetail/matchControlPanel";
import TeamControlPanel from "./TeamControlPanel";

export default function TeamDetail() {
    const { id } = useParams();
    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchData = async () => {
            setLoading(true);
            try {
                let teamData = await getTeam(id);
                setData(teamData);
            } catch (error) {
                console.error("Chyba při načítání dat:", error);
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, [id]);

    return (
        <div>
            <Breadcrumbs/>
            <TopBanner data={data}></TopBanner>
            <TeamControlPanel></TeamControlPanel>
        </div>
    );
}
