import axios from "axios";
import UserDto from "../DTOs/UserDto";

export async function getAllUsers(){
    try {
        const response = await axios.get("http://127.0.0.1:8000/api/get_all_users")
        if(response){
            return response.data.map(user => new UserDto(user));
        }
        return [];
    } catch (error){
        return [];
    }
}

export async function getOneUser(id){
    try {
        const response = await axios.get("http://127.0.0.1:8000/api/get_one_user/" + id)
        return response.data
    } catch (error){
        return null;
    }
}

export async function addUser(data){
    try {
        const response = await axios.post("http://127.0.0.1:8000/api/add_user", data);
        if(response){
            window.location.replace('/admin/uzivatele');
        }
    } catch (error){
        console.error(error)
    }
}

export async function deleteUser(id){
    try {
        const response = await axios.get("http://127.0.0.1:8000/api/delete_user/" + id);
        return response.data.map(user => new UserDto(user));
    } catch (error){
        console.error(error)
    }
}

export async function updateUser(data,id){
    try {
        console.log(data)
        await axios.post("http://127.0.0.1:8000/api/update_user/" + id, data);
        window.location.replace('/admin/uzivatele');
    } catch (error){
        console.error(error)
    }
}

export async function getCountOfUsers(){
    try {
        const response = await axios.get("http://127.0.0.1:8000/api/get_all_users")
        if(response){
            return response.data.length
        }
    } catch (error){
        console.error(error)
    }
}
