import React from 'react';
import Link from "./link";

export default function Modal({ type, onClose, onSwitchModal }) {
    const isLogin = type === 'login';

    return (
        <div className="modalContainer">
            <div className="modal">
                <div className="modal-header">
                    <div className="icon-container">
                        <img src="/images/football-icon.svg" alt="Ikona rugbyového míče" />
                    </div>
                    <button className="close-btn" onClick={onClose}>&times;</button>
                </div>
                <h1>Vítejte u SportMatter</h1>

                <form className="login-form">
                    <label htmlFor="email">Email</label>
                    <input type="email" id="email" name="email" required />

                    {!isLogin && (
                        <>
                            <label htmlFor="name">Jméno</label>
                            <input type="text" id="name" name="name" required />
                        </>
                    )}

                    <label htmlFor="password">Heslo</label>
                    <input type="password" id="password" name="password" required />

                    {!isLogin && (
                        <>
                            <label htmlFor="confirmPassword">Potvrďte heslo</label>
                            <input type="password" id="confirmPassword" name="confirmPassword" required />
                        </>
                    )}

                    <Link
                        href={"#"}
                        className={"forgot-password"}
                        text={isLogin ? 'Zapomněli jste heslo?' : ''}
                    />

                    <button type="submit" className="submit-btn">{isLogin ? 'Přihlásit se' : 'Registrovat se'}</button>
                </form>
                <p className="register-text">
                    {isLogin ? 'Ještě nemáte účet? ' : 'Máte již účet? '}
                    <Link
                        href={"#"}
                        text={isLogin ? 'Registrace' : 'Přihlásit se'}
                        onClick={() => onSwitchModal(isLogin ? 'register' : 'login')}
                    />
                </p>
            </div>
        </div>
    );
}
