import React, { useState, useEffect } from 'react';
import HeaderNavigation from "../headerNavigation";
import Input from "../../components/input";
import ContentHome from "./contentHome";
import { getAllGames } from "../../Repositories/GameRepository";

export default function Home() {
    const [date, setDate] = useState("");
    const [games, setGames] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            const today = new Date().toISOString().split("T")[0];
            setDate(today);

            let data = await getAllGames(date);
            setGames(data);
        };

        fetchData().then(r => console.log("Data loaded"));
    }, []);

    const handleSpanClick = () => {
        const inputElement = document.querySelector(".inputDate");
        if (inputElement) {
            inputElement.showPicker();
            inputElement.focus();
        }
    };

    return (
        <div id={"home"}>
            <HeaderNavigation />
            <div className={"lg:px-20 flex justify-end items-end py-10"}>
                <div className="custom-date">
                    <Input
                        className={"flex justify-end items-end w-fit inputDate"}
                        type={"date"}
                        value={date}
                        onChange={(e) => setDate(e.target.value)}
                    />
                    <span className="icon" onClick={handleSpanClick}>
                        <img src={"/images/calendar.svg"} alt={"calendar"} />
                    </span>
                </div>
            </div>
            <ContentHome games={games} />
        </div>
    );
}
