import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { authResetToken } from "../Auth/authResetToken";

export default function ResetPassword() {
    let { token } = useParams();
    const [isValid, setIsValid] = useState(null);
    const [errorMessage, setErrorMessage] = useState("");

    useEffect(() => {
        if (token) {
            authResetToken(token)
                .then(response => {
                    setIsValid(true);
                    console.log("Token valid:", response);
                })
                .catch(error => {
                    setIsValid(false);
                    setErrorMessage(error.message);
                    console.error("Token invalid:", error.message);
                });
        }
    }, [token]);

    return (
        <div>
            <p>Token: {token}</p>
            {isValid === null ? (
                <p>Ověřování tokenu...</p>
            ) : isValid ? (
                <p>Token je platný. Můžete resetovat heslo.</p>
            ) : (
                <p>Token je neplatný nebo vypršel. {errorMessage && `Chyba: ${errorMessage}`}</p>
            )}
        </div>
    );
}
