import axios from 'axios';
import GameDto from '../DTOs/GameDto';

export async function getAllGames(date) {
    try {
        let dateOfDay = date ?? new Date().toISOString().split("T")[0];
        const url = `${process.env.REACT_APP_API_URL}/dateGames/${dateOfDay}`;
        const response = await axios.get(url);
        if (response.data && response.data.member) {
            console.log(response.data);
            const gameData = response.data.member;

            const games = await Promise.all(
                gameData.map(async (game) => {
                    const [homeTeam, awayTeam, supervisor, referees, league] = await Promise.all([
                        fetchData(game.homeTeamId),
                        fetchData(game.awayTeamId),
                        fetchData(game.superviserId),
                        fetchReferees(game.refereeGames),
                        fetchData(game.leagueId)
                    ]);

                    const [country, sport] = await Promise.all([
                        fetchData(league?.countryId),
                        fetchData(league?.sportId)
                    ]);

                    return new GameDto({
                        id: game.id,
                        lap: game.lap,
                        date: game.date_of_game,
                        parametrs: game.parametrs,
                        homeTeam,
                        awayTeam,
                        supervisor,
                        referees,
                        league,
                        country,
                        sport
                    });
                })
            );
            return games;
        }
        return [];
    } catch (error) {
        console.error("Chyba při načítání her:", error);
        return [];
    }
}

async function fetchData(apiUrl) {
    if (!apiUrl) return null;
    try {
        const response = await axios.get("https://simon.pekneweby.com" + apiUrl);
        return response.data;
    } catch (error) {
        console.error("Chyba při načítání dat:", error);
        return null;
    }
}

async function fetchReferees(refereeGames) {
    if (!refereeGames) return [];
    return Promise.all(
        refereeGames.map(async (refGame) => {
            try {
                const response = await axios.get("https://simon.pekneweby.com" + refGame.referee);
                return response.data;
            } catch (error) {
                console.error("Chyba při načítání rozhodčího:", error);
                return null;
            }
        })
    );
}
