import React from "react";
import ImageWithBorder from "../../components/ImageWithBorder";
import Title from "../../components/Title";
import Star from "../../components/Star";
import Text from "../../components/Text";
import FlexDiv from "../../components/FlexDiv";

export default function TopBanner({data}) {
    return (
        <div className="top-banner flex">
            <div className={"flex flex-row items-start gap-10 lg:px-20 py-5"}>
                <ImageWithBorder className={"h-28 w-28 object-contain"} logo={data.image_src}></ImageWithBorder>
                <div>
                    <div className={"flex flex-row items-center gap-3"}>
                        <Title className={"font-bold text-3xl"} text={data.name}></Title>
                        <div
                            className={`text-white flex flex-col items-center cursor-pointer ${props.isFavorited ? "" : "not-favorite"}`}
                            onClick={() => handleFavoriteClick(props.id)}
                        >
                            <span className="text-3xl">⭐</span>
                            <p className="text-lg font-bold">(1. místo)</p>
                        </div>
                    </div>
                    <FlexDiv className={"gap-3 flex mt-5"}>
                        <Text className={"font-bold"} text={"Stadion:"}></Text>
                        <Text text={data.stadium_id.name}></Text>
                    </FlexDiv>
                    <FlexDiv className={"gap-3 flex mt-1"}>
                        <Text className={"font-bold"} text={"Kapacita:"}></Text>
                        <Text text={data.stadium_id.capacity}></Text>
                    </FlexDiv>
                    <FlexDiv className={"gap-3 flex mt-1"}>
                        <Text className={"font-bold"} text={"Trenér:"}></Text>
                        <Text text={data.coach}></Text>
                    </FlexDiv>
                </div>
            </div>
        </div>
    );
}
