import React, { useState } from "react";
import MatchPanel from "../../components/matchPanel";
import Match from "../../components/match";
import Text from "../../components/Text";

export default function ContentPlayer({ data }) {

    if (!data || data.length === 0) {
        return (
           <Text>Nedostupná data...</Text>
        );
    }

    return (
        <div className="lg:px-20 py-10">
            <MatchPanel headline="Výsledky">
                {data.data.map((match) => {
                    const game = match.game;
                    const dateObj = new Date(game.date_of_game);
                    const date = dateObj.toLocaleDateString();
                    const time = dateObj.toLocaleTimeString();

                    return (
                        <Match
                            key={match.id}
                            time={`${date} ${time}`}
                            team1={game.home_team_id.name}
                            team1Img={game.home_team_id.image_src}
                            team2={game.away_team_id.name}
                            team2Img={game.away_team_id.image_src}
                            score1={game.parametrs ? game.parametrs.goals_home : "-"}
                            score2={game.parametrs ? game.parametrs.goals_away : "-"}
                            id={match.id}
                        />
                    );
                })}
            </MatchPanel>
        </div>
    );
}
