import { useEffect, useState } from "react";
import LinkNavigation from "../components/linkNavigation";
import { menuRepository } from "../Repositories/MenuRepository";

export default function HeaderNavigation() {
    const [menuItems, setMenuItems] = useState([]);

    useEffect(() => {
        async function fetchMenuItems() {
            const data = await menuRepository();
            if (data) {
                setMenuItems(data);
            }
        }
        fetchMenuItems().then(r => console.log("Menu items loaded"));
    }, []);

    return (
        <nav className="flex lg:flex-1 items-center gap-12 lg:px-20 py-5 headerNavigation" aria-label="Global">
            {menuItems.map((item, index) => (
                <LinkNavigation
                    key={index}
                    classNameDiv="flex gap-3 flex-row-reverse"
                    className="text-white text-l uppercase font-bold"
                    src={String(item.img_src).replace(/["']/g, "")}
                    text={item.name}
                    alt={item.name}
                    href={item.url}
                />
            ))}
        </nav>
    );
}
