export default class GameDto {
    constructor(data) {
        const dateTime = data.date_of_game ? new Date(data.date_of_game) : null;
        this.id = data.id;
        this.lap = data.lap || null;
        this.date = dateTime ? dateTime.toLocaleDateString('cs-CZ') : null;
        this.time_of_game = dateTime ? dateTime.toLocaleTimeString('cs-CZ', { hour: '2-digit', minute: '2-digit' }) : null;
        this.parametrs = this.parseParametrs(data.parametrs || []);
        this.homeTeam = data.home_team_id ? { id: data.home_team_id.id, name: data.home_team_id.name, coach: data.home_team_id.coach, image: data.home_team_id.image_src } : null;
        this.awayTeam = data.away_team_id ? { id: data.away_team_id.id, name: data.away_team_id.name, coach: data.away_team_id.coach, image: data.away_team_id.image_src } : null;
        this.supervisor = data.superviser_id || [];
        this.league = data.league_id ? { id: data.league_id.id, name: data.league_id.name, association: data.league_id.assocation } : null;
        this.sport = data.league_id?.sport ? { id: data.league_id.sport.id, name: data.league_id.sport.name, url: data.league_id.sport.url } : null;
    }

    parseParametrs(parametrs) {
        const parsed = {};
        parametrs.forEach(param => {
            const [key, value] = param.split(':');
            if (key && value) {
                parsed[key] = parseInt(value, 10);
            }
        });
        return parsed;
    }

    getGameSummary() {
        return `${this.homeTeam?.name || 'N/A'} vs ${this.awayTeam?.name || 'N/A'} (${this.league?.name || 'No league'}, ${this.sport?.name || 'No sport'})`;
    }
}
