import Link from "./link";
import {login} from "../Auth/login";
import UserLocalStorage from "../Auth/userAuth";
import React, {useState} from "react";

export default function Modal({ type, onClose, onSwitchModal }) {
    const isLogin = type === 'login';
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, errorState] = useState('');
    const [password_again, setPasswordAgain] = useState('');
    const [name, setName] = useState('');

        const handleSubmit = async (event) => {
            event.preventDefault();
            if(isLogin){
            try {
                const data = await login(email, password);
                if(data){
                    UserLocalStorage.setUser(data.data)
                  //  window.location.replace('/')
                } else {
                    errorState(data.message);
                }
            } catch(error){
                errorState("Něco se pokazilo");
            }} else {

            }
        }


    return (
        <div className="modalContainer">
            <div className="modal">
                <div className="modal-header">
                    <div className="icon-container">
                        <img src="/images/football-icon.svg" alt="Ikona rugbyového míče" />
                    </div>
                    <button className="close-btn" onClick={onClose}>&times;</button>
                </div>
                <h1>Vítejte u SportMatter</h1>

                <form onSubmit={handleSubmit} className="login-form">
                    <label htmlFor="email">Email</label>
                    <input type="email" id="email" name="email" required onChange={(e) => setEmail(e.target.value)}/>

                    {!isLogin && (
                        <>
                            <label htmlFor="name">Jméno</label>
                            <input type="text" id="name" name="name" required onChange={(e) => setName(e.target.value)}/>
                        </>
                    )}

                    <label htmlFor="password">Heslo</label>
                    <input type="password" id="password" name="password" required
                           onChange={(e) => setPassword(e.target.value)}/>

                    {!isLogin && (
                        <>
                            <label htmlFor="confirmPassword">Potvrďte heslo</label>
                            <input type="password" id="confirmPassword" name="confirmPassword" required onChange={(e) => setPasswordAgain(e.target.value)}/>
                        </>
                    )}

                    <Link
                        href={"#"}
                        className={"forgot-password"}
                        text={isLogin ? 'Zapomněli jste heslo?' : ''}
                    />
                    <p className="text-red-950">
                        {error}
                    </p>
                    <button type="submit" className="submit-btn">{isLogin ? 'Přihlásit se' : 'Registrovat se'}</button>
                </form>
                <p className="register-text">
                    {isLogin ? 'Ještě nemáte účet? ' : 'Máte již účet? '}
                    <Link
                        href={"#"}
                        text={isLogin ? 'Registrace' : 'Přihlásit se'}
                        onClick={() => onSwitchModal(isLogin ? 'register' : 'login')}
                    />
                </p>
            </div>
        </div>
    );
}
