import React, {useEffect, useState} from "react";
import {deleteRecipe, getAllRecipe, getAllRecipeAdmin, hiddenRecipe} from "../../../Repositories/RecipeRepository";
import {paginate} from "../../../Services/paginationUtils";
import {register} from "../../../Auth/register";

export default function AdminRecipes() {
    const [recipes, setRecipes] = useState([]);
    const [load, setLoad] = useState(true);
    const [currentPage, setCurrentPage] = useState(1);
    const recipesPerPage = 7;

    useEffect(() => {
        async function fetchData() {
            try {
                const data = await getAllRecipeAdmin();
                setRecipes(data);
                setLoad(false)
            } catch (error) {
                console.error(error);
            }
        }

        fetchData();
    }, []);

    const currentRecipes = paginate(recipes, currentPage, recipesPerPage);
    const handlePageChange = (pageNumber) => setCurrentPage(pageNumber);

    const handleHidden = async ($id) => {
        try {
          const data =  await hiddenRecipe($id);
          setRecipes(data)
        } catch (error) {
            console.error(error);
        }

    }

    const handleDelete = async ($id) => {
        try {
            const data =  await deleteRecipe($id);
            setRecipes(data)
        } catch (error) {
            console.error(error);
        }

    }

    if(load){
        return (<div></div>);
    }

    return (
        <div className="px-4 sm:px-6 lg:px-8 mt-12">
            <div className="sm:flex sm:items-center">
                <div className="sm:flex-auto">
                    <h1 className="font-bold text-xl leading-6 text-gray-900">Recepty</h1>
                    <p className="mt-2 text-sm text-gray-700">
                    </p>
                </div>
                <div className="mt-4 sm:ml-16 sm:mt-0 sm:flex-none">
                    <a
                        href="/admin/pridatRecept"
                        className="block rounded-md bg-indigo-600 px-3 py-2 text-center text-sm font-semibold text-white shadow-sm hover:bg-indigo-500 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600"
                    >
                        Přidat recept
                    </a>
                </div>
            </div>
            <div className="mt-8 flow-root">
                <div className="-mx-4 -my-2 overflow-x-auto sm:-mx-6 lg:-mx-8">
                    <div className="inline-block min-w-full py-2 align-middle sm:px-6 lg:px-8">
                        <table className="min-w-full divide-y divide-gray-300">
                            <thead>
                            <tr>
                                <th scope="col" className="px-3 py-3.5 text-left text-sm font-semibold text-gray-900">
                                    Obrázek
                                </th>
                                <th scope="col" className="py-3.5 pl-4 pr-3 text-left text-sm font-semibold text-gray-900 sm:pl-0">
                                    Nadpis
                                </th>
                                <th scope="col" className="px-3 py-3.5 text-left text-sm font-semibold text-gray-900">
                                    Ingredience
                                </th>
                                <th scope="col" className="px-3 py-3.5 text-left text-sm font-semibold text-gray-900">
                                    Čas přípravy
                                </th>
                                <th scope="col" className="px-3 py-3.5 text-left text-sm font-semibold text-gray-900">
                                    Viditelnost
                                </th>
                                <th scope="col" className="relative py-3.5 pl-3 pr-4 sm:pr-0">
                                    <span className="sr-only">Edit</span>
                                </th>
                            </tr>
                            </thead>
                            <tbody className="divide-y divide-gray-200">
                            {currentRecipes.map((recipe) => (
                                <tr key={recipe.id} className={"text-left h-40"}>
                                    <td className="whitespace-nowrap py-4 pl-4 pr-3 text-sm font-medium text-gray-900 sm:pl-0">
                                        <img className={"rounded-lg"} width={"100px"} src={recipe.imageUrl} />
                                    </td>
                                    <td className="whitespace-nowrap px-3 py-4 text-sm text-gray-500">{recipe.headline}</td>
                                    <td className="whitespace-nowrap px-3 py-4 text-sm text-gray-500">{recipe.ingredients}</td>
                                    <td className="whitespace-nowrap px-3 py-4 text-sm text-gray-500">{recipe.time}m</td>
                                    <td className="whitespace-nowrap px-3 py-4 text-sm text-gray-500">{recipe.getActive()}</td>
                                    <td className="relative whitespace-nowrap py-4 pl-3 pr-4 text-right text-sm font-medium sm:pr-0">
                                        <a href={"/admin/editRecept/" + recipe.id} className="text-indigo-600 hover:text-indigo-900 mr-5 cursor-pointer">
                                            Upravit<span className="sr-only">, {recipe.id}</span>
                                        </a>
                                        <a onClick={() => handleHidden(recipe.id)} className="text-indigo-600 hover:text-indigo-900 mr-5 cursor-pointer">
                                            Přepnout viditelnost<span className="sr-only">, {recipe.id}</span>
                                        </a>
                                        <a onClick={() => handleDelete(recipe.id)} className="text-indigo-600 hover:text-indigo-900 mr-5 cursor-pointer">
                                            Odstranit<span className="sr-only">, {recipe.id}</span>
                                        </a>
                                    </td>
                                </tr>
                            ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
            <div className="mt-7">
                {recipes.length > recipesPerPage && (
                    <nav className="bg-white px-4 py-3 flex items-center justify-between border-t border-gray-200 sm:px-6">
                        <div className="hidden sm:block">
                            <p className="text-sm text-gray-700">
                                {((currentPage - 1) * recipesPerPage) + 1} - {Math.min((currentPage * recipesPerPage), recipes.length)} z {recipes.length} receptů
                            </p>
                        </div>
                        <div className="flex-1 flex justify-between sm:justify-end">
                            <button
                                onClick={() => handlePageChange(currentPage - 1)}
                                disabled={currentPage === 1}
                                className="relative inline-flex items-center px-4 py-2 border border-gray-300 bg-white text-sm font-medium text-gray-700 hover:bg-gray-50"
                            >
                                Předešlé
                            </button>
                            <button
                                onClick={() => handlePageChange(currentPage + 1)}
                                disabled={currentRecipes.length < recipesPerPage}
                                className="ml-3 relative inline-flex items-center px-4 py-2 border border-gray-300 bg-white text-sm font-medium text-gray-700 hover:bg-gray-50"
                            >
                                Další
                            </button>
                        </div>
                    </nav>
                )}
            </div>
        </div>
    )
}
