export default class GameDto {
    constructor(data) {
        const dateTime = data.date ? new Date(data.date) : null;
        this.id = data.id;
        this.lap = data.lap || null;
        this.date = dateTime ? dateTime.toLocaleDateString('cs-CZ') : null;
        this.time_of_game = dateTime ? dateTime.toLocaleTimeString('cs-CZ', { hour: '2-digit', minute: '2-digit' }) : null;
        this.parametrs = data.parametrs || [];
        this.homeTeam = data.homeTeam || null;
        this.awayTeam = data.awayTeam || null;
        this.supervisor = data.supervisor || null;
        this.referees = data.referees || [];
        this.league = data.league || null;
        this.country = data.country || null;
        this.sport = data.sport || null;
    }

    getGameSummary() {
        return `${this.homeTeam?.name || 'N/A'} vs ${this.awayTeam?.name || 'N/A'} (${this.league?.name || 'No league'}, ${this.country?.name || 'No country'}, ${this.sport?.name || 'No sport'})`;
    }
}
