import Input from "../../../components/input";
import React, {useState} from "react";
import Select from "../../../components/select";
import {addUser} from "../../../Repositories/UserRepository";

export function CreateUser() {
    const [formData, setFormData] = useState({
        email: '',
        password: null,
        access: null,
    });
    const [showPassword, setShowPassword] = useState(false);

    const options = [
        { value: 0, label: "Uživatel" },
        { value: 1, label: "Administrátor" }
    ];

    const togglePasswordVisibility = () => {
        setShowPassword(!showPassword);
    };


    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        const formDataToSend = new FormData();
        formDataToSend.append('email', formData.email);
        formDataToSend.append('password', formData.password);
        formDataToSend.append('access', formData.access);

        await addUser(formDataToSend);
    };

    return (
        <div className={"formObal"}>
            <form onSubmit={handleSubmit} encType={"multipart/form-data"}>
                <h1 className={"text-3xl font-bold text-center underline"}>Vytvořit uživatele</h1>
                <Input type={"text"} name={"email"} label={"Email"} value={formData.email}
                       onChange={handleInputChange}/>
                <Input type={showPassword ? "text" : "password"} name={"password"} label={"Heslo"} onChange={handleInputChange}/>
                <button type="button" onClick={togglePasswordVisibility}>
                    {showPassword ? "Skrýt heslo" : "Zobrazit heslo"}
                </button>
                <Select name={"access"} label={"Práva"} options={options} onChange={handleInputChange}></Select>
                <div className={"flex flex-col items-center"}>
                    <button className={"bg-white text-black font-bold py-2 w-2/12 mt-4 text-center rounded-2xl"} type="submit">Vytvořit</button>
                </div>
            </form>
        </div>
    );
}
