import React, { useState } from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';

const MobileAppSuggestion = () => {
    const [showSuggestion, setShowSuggestion] = useState(/Mobi|Android/i.test(navigator.userAgent)); // Detekce mobilního zařízení

    if (showSuggestion) {
        return (
            <div style={{ padding: '20px', textAlign: 'center', backgroundColor: '#f0f0f0' }}>
                <p><strong>Stáhněte si mobilní aplikaci pro lepší zážitek!</strong></p>
                <div>
                    <a
                        href="/download-app"
                        style={{ marginRight: '10px', color: 'blue', textDecoration: 'underline' }}
                    >
                        Stáhnout aplikaci
                    </a>
                    <button
                        onClick={() => setShowSuggestion(false)}
                        style={{ color: 'blue', textDecoration: 'underline', background: 'none', border: 'none', cursor: 'pointer' }}
                    >
                        Zůstat na webu
                    </button>
                </div>
            </div>
        );
    }

    return <App />;
};

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
    <React.StrictMode>
        <MobileAppSuggestion />
    </React.StrictMode>
);

reportWebVitals();
