import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { authResetToken } from "../Auth/authResetToken";

export default function ResetPassword() {
    let { token } = useParams();
    const [isValid, setIsValid] = useState(null);
    const [errorMessage, setErrorMessage] = useState("");

    useEffect(() => {
        if (token) {
            authResetToken(token)
                .then(response => {
                    setIsValid(true);
                    console.log("Token valid:", response);
                })
                .catch(error => {
                    setIsValid(false);
                    setErrorMessage(error.message);
                    console.error("Token invalid:", error.message);
                });
        }
    }, [token]);

    return (
        <div className={"mt-10 h-dvh"}>
            {isValid === null ? (
                <p>Ověřování tokenu...</p>
            ) : isValid ? (
                <p>Token je platný. Můžete resetovat heslo.</p>
            ) : (
                <h1 className={"text-center text-xl font-bold"}>Token je neplatný nebo vypršel</h1>
            )}
        </div>
    );
}
