import React, { useState, useEffect } from 'react';
import HeaderNavigation from "../headerNavigation";
import Input from "../../components/input";
import ContentHome from "./contentHome";
import { getAllGames } from "../../Repositories/GameRepository";

export default function Home() {
    const [date, setDate] = useState(() => new Date().toISOString().split("T")[0]);
    const [games, setGames] = useState([]);
    const [loading, setLoading] = useState(false); // Stav pro indikaci načítání

    useEffect(() => {
        const fetchData = async () => {
            setLoading(true); // Spustí načítání
            let data = await getAllGames(date);
            setGames(data);
            setLoading(false); // Když jsou data načtena, nastaví se na false
        };

        if (date) {
            fetchData().then(() => console.log("Data loaded"));
        }
    }, [date]);

    const handleSpanClick = () => {
        const inputElement = document.querySelector(".inputDate");
        if (inputElement) {
            inputElement.showPicker();
            inputElement.focus();
        }
    };

    return (
        <div id={"home"}>
            <HeaderNavigation />
            <div className={"lg:px-20 flex justify-end items-end py-10"}>
                <div className="custom-date">
                    <Input
                        className={"flex justify-end items-end w-fit inputDate"}
                        type={"date"}
                        value={date}
                        onChange={(e) => setDate(e.target.value)}
                    />
                    <span className="icon" onClick={handleSpanClick}>
                        <img src={"/images/calendar.svg"} alt={"calendar"} />
                    </span>
                </div>
            </div>

            {loading ? (
                <div className="spinner-container flex justify-center items-center">
                    <div className="spinner"></div>
                    <p>Načítám data...</p>
                </div>
            ) : (
                <ContentHome games={games} />
            )}
        </div>
    );
}
