import RatingStar from "../../components/ratingStar";
import Title from "../../components/title";
import Link from "../../components/link";
import {getHomeRecipes} from "../../Repositories/RecipeRepository";
import {useEffect, useState} from "react";
import Recipe from "../../components/recipe";

export default function Products() {
    const [recipes, setRecipes] = useState([]);

    useEffect(() => {
        async function fetchData() {
            try {
                const data = await getHomeRecipes();
                setRecipes(data);
            } catch (error) {
                console.error(error);
            }
        }

        fetchData();
    }, []);


    return (
        <div className="bg-white">
            <div className="mx-auto max-w-2xl px-4 py-12 sm:px-6 sm:py-24 lg:max-w-7xl lg:px-8">
                <div className="flex items-baseline justify-between">
                    <Title className="p-1.5 text-base font-semibold text-md leading-6 uppercase mb-7" text="Recepty s nejvyšším hodnocením"/>
                    <Link href="/recepty" text="Všechny recepty"><span className="ml-1" aria-hidden="true">→</span></Link>
                </div>

                <div className="grid grid-cols-1 gap-x-6 gap-y-10 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 xl:gap-x-8">
                    {recipes.map((recipe) => (
                        <Recipe key={recipe.id} recipe={recipe} />
                    ))}
                </div>
            </div>
        </div>
    );
}
