import apiService from './ApiService';  // Importujeme ApiService

export async function login(email, password) {
    try {
        // Odeslání POST požadavku pro přihlášení
        const loginData = await apiService.post('/login', { email, password });

        const token = loginData.data.token; // Předpokládám, že token je v data.token
        apiService.setToken(token);  // Nastavíme token pro další požadavky

        // Získání uživatelských dat s autentizací
        const userData = await apiService.get('/user/me');

        return userData; // Vrátíme získaná uživatelská data
    } catch (error) {
        console.error("Chyba při přihlašování nebo získávání uživatelských dat:", error);
        throw error;
    }
}
