import React, { useEffect, useState } from "react";
import MatchPanel from "../../components/matchPanel";
import Match from "../../components/match";
import PanelBlock from "../../components/PanelBlock";
import LoadingSpinner from "../../components/LoadingSpinner";
import {getTeamResults, getTeamSchedule} from "../../Repositories/TeamRepository";
import {useParams} from "react-router-dom";
import {formatDate} from "../../Services/Validator";
import GameDto from "../../DTOs/GameDto";

export default function ContentTeam({ type, data, id }) {
    const [isLoaded, setIsLoaded] = useState(false);
    const [teamData, setTeamData] = useState([]);

    useEffect(() => {
        setIsLoaded(false);
        if (type === "results") {
            getTeamResults(id).then((results) => {
                setTeamData(results);
                setIsLoaded(true);
            });
        } else if (type === "program") {
            getTeamSchedule(id).then((schedule) => {
                setTeamData(schedule);
                setIsLoaded(true);
            });
        } else {
            setIsLoaded(true);
        }
    }, [type, id]);

    const groupedPlayers = data.players.reduce((acc, player) => {
        if (!acc[player.position]) {
            acc[player.position] = [];
        }
        acc[player.position].push(player);
        return acc;
    }, {});

    if (!isLoaded) {
        return (
            <LoadingSpinner>
                <p>Načítám data...</p>
            </LoadingSpinner>
        );
    }

    return (
        <div className="lg:px-20 py-10">
            {type === "results" && (
                <MatchPanel headline="Výsledky">
                    {teamData.map((match) => (
                        <Match key={match.id} time={formatDate(match.date_of_game)} team1={match.home_team_id.name}  team1Img={match.home_team_id.image_src} team2={match.away_team_id.name} team2Img={match.away_team_id.image_src} score1={match.parameters.goals_home} />
                    ))}
                </MatchPanel>
            )}
            {type === "program" && (
                <MatchPanel headline="Program zápasů">
                    {teamData.map((match) => (
                        <Match key={match.id} time={formatDate(match.date_of_game)} {...match} />
                    ))}
                </MatchPanel>
            )}
            {type === "lineUp" && (
                <MatchPanel headline="Soupiska týmů">
                    {Object.entries(groupedPlayers).map(([position, players]) => (
                        <PanelBlock key={position} position={position} players={players} />
                    ))}
                </MatchPanel>
            )}
        </div>
    );
}
