import userAuth from "../Auth/userAuth";
import {deleteComment} from "../Repositories/RecipeRepository";

export default function Comment(prop){
    const handleDelete = async ($id) => {
        try {
             const data =  await deleteComment($id);
             if(data){
                 window.location.reload();
             }
        } catch (error) {
            console.error(error);
        }

    }


    return(
        <article class="p-6 text-base bg-white border-t border-gray-200 dark:border-gray-700 dark:bg-gray-900 rounded-2xl mt-5">
            <footer class="flex justify-between items-center mb-2">
                <div class="flex items-center text-white gap-2">
                    <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="1.5" stroke="currentColor" className="w-6 h-6">
                        <path strokeLinecap="round" strokeLinejoin="round" d="M17.982 18.725A7.488 7.488 0 0 0 12 15.75a7.488 7.488 0 0 0-5.982 2.975m11.963 0a9 9 0 1 0-11.963 0m11.963 0A8.966 8.966 0 0 1 12 21a8.966 8.966 0 0 1-5.982-2.275M15 9.75a3 3 0 1 1-6 0 3 3 0 0 1 6 0Z" />
                    </svg>
                    <p>{prop.email}</p>
                </div>
            </footer>
            <p className="text-gray-500 dark:text-gray-400">{prop.text}</p>
            {userAuth.isAdmin() && <a onClick={() => handleDelete(prop.id)} className={"text-white inline-flex items-center py-2.5 px-4 text-xs font-medium text-center bg-primary-700 rounded-lg focus:ring-4 focus:ring-primary-200 dark:focus:ring-primary-900 hover:bg-primary-800 mt-5 cursor-pointer"}>Smazat</a>}
        </article>
    );
}
