import axios from 'axios';

export async function login(email, password) {
    try {
        const response = await axios.post('https://simon.pekneweby.com/api/login', {
            email: email,
            password: password
        });

        const token = response.data.data.token; // Předpokládám, že token je zde
        console.log(token);

        if (!token) {
            throw new Error("Token nebyl vrácen.");
        }

        // Získání dat o uživateli
        const userResponse = await axios.get('https://simon.pekneweby.com/api/user/me', {
            headers: {
                Authorization: `Bearer ${token}`
            }
        });

        return userResponse.data;
    } catch (error) {
        console.error("Chyba při přihlašování nebo získávání uživatelských dat:", error);
        throw error;
    }
}
