class UserLocalStorage {
    static getUser() {
        const userString = localStorage.getItem("loginUser");
        return userString ? JSON.parse(userString) : null;
    }

    static getUserId() {
        const user = UserLocalStorage.getUser();
        return user ? user.id : null; // Pokud uživatel nemá `id`, bude to null
    }

    static setUser(user) {
        localStorage.setItem("loginUser", JSON.stringify(user));
    }

    static getUserEmail() {
        const user = UserLocalStorage.getUser();
        return user && user.email ? user.email : null; // Přidání kontroly existence `email`
    }

    static isUserLoggedIn() {
        return !!UserLocalStorage.getUser();
    }

    static isAdmin() {
        const user = UserLocalStorage.getUser();
        return user && user.role === "Admin"; // Kontrola role
    }
}
