export default class RecipeDto {
    id;
    isActive;
    headline;
    text;
    imageUrl;
    ingredients;
    time;
    avgScore;

    constructor(data) {
        this.id = data[0].id;
        this.isActive = data[0].isActive;
        this.headline = data[0].headline;
        this.text = data[0].text;
        this.imageUrl = data[0].imageUrl;
        this.ingredients = data[0].ingredients.join(", ");
        this.time = data[0].preparationTime;
        this.avgScore = Math.round(parseFloat(data.avg_score));
    }

    getActive(){
        switch (this.isActive){
            case 0:
                return "Neaktivní"
            case 1:
                return "Aktivní"
            default:
                return "Aktivní"
        }
    }
}
