import React, {useState} from "react";
import MatchPanel from "../../components/matchPanel";
import Match from "../../components/match";
import LoadingSpinner from "../../components/LoadingSpinner";

export default function ContentPlayer(data) {
    const [isLoaded, setIsLoaded] = useState(true);

    if (!isLoaded) {
        return (
            <LoadingSpinner>
                <p>Načítám data...</p>
            </LoadingSpinner>
        );
    }


    return (
        <div className="lg:px-20 py-10">
            <MatchPanel headline="Výsledky">
                {data.map((match) => (
                    <Match key={match.id} time={`${match.date} ${match.time_of_game}`} team1={match.homeTeam.name} team1Img={match.homeTeam.image}
                           team2={match.awayTeam.name} team2Img={match.awayTeam.image} score1={match.parametrs.goals_home} score2={match.parametrs.goals_away}
                           id={match.id}
                    />
                ))}
            </MatchPanel>
        </div>
    );
}
