import React, { useState } from 'react'
import { Dialog } from '@headlessui/react'
import { Bars3Icon, XMarkIcon } from '@heroicons/react/24/outline'
import UserLocalStorage from '../../Auth/UserAuth';
import userAuth from "../../Auth/UserAuth";
import Input from "../../components/input";
import Modal from "../../components/modal";
import Profile from "../User/Profile";

export default function Header() {
    const [mobileMenuOpen, setMobileMenuOpen] = useState(false)
    let [search, setSearch] = useState('');

    const [isModalOpen, setModalOpen] = useState(false);
    const [modalType, setModalType] = useState('login');
    const [isProfileOpen, setProfileOpen] = useState(false);

    const openModal = (type) => {
        setModalType(type);
        setModalOpen(true);
    };

    const openProfile = () => {
        setProfileOpen(true);
    }


    const closeModal = () => {
        setModalOpen(false);
        setProfileOpen(false);
    };

    const switchModal = (type) => {
        setModalType(type);
    };

    console.log(search);

    return (
        <header>
            <nav className={
                `mx-auto flex items-center justify-between p-6 lg:px-20`
            } aria-label="Global">
                <div className="flex lg:flex-1 items-center gap-3">
                    <a href="/" className="-m-1.5 p-1.5 text-base font-semibold leading-6 text-white">
                        <img
                            src={"/images/logo.png"}
                            alt={"logo"}
                        />
                    </a>
                </div>
                <div className="flex lg:hidden">
                    <button
                        type="button"
                        className="-m-2.5 inline-flex items-center justify-center rounded-md p-2.5 text-gray-400"
                        onClick={() => setMobileMenuOpen(true)}
                    >
                        <span className="sr-only">Open main menu</span>
                        <Bars3Icon className="h-6 w-6" aria-hidden="true" />
                    </button>
                </div>
                <div className="hidden lg:flex lg:gap-x-12 relative">
                    <img className={"searchIcon"} src={"/images/search.svg"} alt={"login"}/>
                    <Input
                        type={"search"}
                        className={"vyhledavac"}
                        placeholder={"Vyhledat"}
                        value={search}
                        onChange={(e) => {
                            const value = e.target.value;
                            if (value.length >= 3) {
                                setSearch(value);
                            }
                        }}
                    />
                </div>
                <div className="hidden lg:flex lg:flex-1 lg:justify-end gap-6">
                    {UserLocalStorage.isUserLoggedIn() && (
                        <div onClick={() => {
                            openProfile();
                        }} className={"buttonLogin flex gap-2"}>
                            <img src={"/images/login.svg"} alt={"login"}/>
                            <p
                                className={userAuth.isAdmin() ? "text-sm font-semibold leading-6 text-black uppercase" : "text-sm font-semibold leading-6 text-white uppercase"}
                            >
                                Profil
                            </p>

                        </div>
                    )}
                    <div onClick={() => {
                        if (UserLocalStorage.isUserLoggedIn()) {
                            UserLocalStorage.logout();
                        } else {
                            openModal('login');
                        }
                    }} className={"buttonLogin flex gap-2"}>
                        <img src={"/images/login.svg"} alt={"login"}/>
                        <p
                            className={userAuth.isAdmin() ? "text-sm font-semibold leading-6 text-black uppercase" : "text-sm font-semibold leading-6 text-white uppercase"}
                        >
                            {UserLocalStorage.isUserLoggedIn() ? "Odhlásit se" : "Přihlášení"}
                        </p>

                    </div>
                </div>
            </nav>
            <Dialog as="div" className="lg:hidden" open={mobileMenuOpen} onClose={setMobileMenuOpen}>
                <div className="fixed inset-0 z-10"/>
                <Dialog.Panel
                    className="fixed inset-y-0 right-0 z-10 w-full overflow-y-auto bg-gray-900 px-6 py-6 sm:max-w-sm sm:ring-1 sm:ring-white/10">
                    <div className="flex items-center justify-between">
                        <a href="/public" className="-m-1.5 p-1.5 text-base font-semibold leading-6 text-white">
                            <i style={iconStyle} className="fa-solid fa-apple-whole fa-rotate-by fa-4xl mb-1"></i>
                        </a>
                        <button
                            type="button"
                            className="-m-2.5 rounded-md p-2.5 text-gray-400"
                            onClick={() => setMobileMenuOpen(false)}
                        >
                            <span className="sr-only">Close menu</span>
                            <XMarkIcon className="h-6 w-6" aria-hidden="true" />
                        </button>
                    </div>
                    <div className="mt-6 flow-root">
                        <div className="-my-6 divide-y divide-gray-500/25">
                            <div className="py-6">
                                <a href={UserLocalStorage.isUserLoggedIn() ? "/logout" : "/login"}
                                   className={userAuth.isAdmin() ? "text-sm font-semibold leading-6 text-white" : "text-sm font-semibold leading-6 text-white"}>
                                    {UserLocalStorage.isUserLoggedIn() ? "Odhlásit se" : "Přihlásit"} <span
                                    aria-hidden="true">&rarr;</span>
                                </a>

                            </div>
                        </div>
                    </div>
                </Dialog.Panel>
            </Dialog>
            {isModalOpen && (
                <Modal
                    type={modalType}
                    onClose={closeModal}
                    onSwitchModal={switchModal}
                />
            )}
            {isProfileOpen && (
                <Profile
                    onClose={closeModal}
                    username={UserLocalStorage.getUsername()}
                />
            )}
        </header>
    )
}

const iconStyle = {
    transform: "rotate(20deg)",
    fontSize: "2rem",
    color: "white"
}
