import React from 'react';

export default function Input({
                                  value = "",
                                  onChange = () => {}, // Provide a default no-op function
                                  label,
                                  type = "text",
                                  name,
                                  className,
                                  required = true,
                                  placeholder = ""
                              }) {
    return (
        <div className={"flex flex-col"}>
            {label && <label htmlFor={name}>{label}</label>}
            <input
                id={name}
                required={required}
                className={className}
                type={type}
                name={name}
                value={value}
                placeholder={placeholder}
                onChange={onChange}
            />
        </div>
    );
}
