    return (
        <div className="rounded-lg flex items-center gap-6 w-fit matchDetailTeam matchDetailTeamAway">
            <div className={`text-white flex flex-col items-center cursor-pointer ${props.isFavorited ? "" : "not-favorite"}`} onClick={() => handleFavoriteClick(props.id)}>
