import { useParams } from "react-router-dom";
import Breadcrumbs from "../../components/breadcrumbs";
import MatchResultPanel from "../../components/matchDetail/matchResultPanel";
import MatchControlPanel from "../../components/matchDetail/matchControlPanel";
import { useEffect, useState } from "react";
import { getGameById } from "../../Repositories/GameRepository";

export default function MatchDetail() {
    let { id } = useParams();
    let [breadcrumbSport, setBreadcrumbSport] = useState("");
    let [breadcrumbLeague, setBreadcrumbLeague] = useState("");
    let [sportHref, setSportHref] = useState("/");
    let [leagueHref, setLeagueHref] = useState("/");
    let [data, setData] = useState(null); // Inicializujeme jako null
    let [loading, setLoading] = useState(true); // Přidáme stav pro načítání

    useEffect(() => {
        const fetchData = async () => {
            setLoading(true);
            try {
                let gameData = await getGameById(id);
                setBreadcrumbSport(gameData.sport.name);
                setBreadcrumbLeague(gameData.league.name);
                setSportHref(`/${gameData.sport.url}`);
                setLeagueHref(`/league/${gameData.league.id}`);
                setData(gameData);
            } catch (error) {
                console.error("Chyba při načítání dat:", error);
            } finally {
                setLoading(false);
            }
        };

        fetchData().then(r => console.log("Data načtena"));
    }, [id]);

    if (loading) {
        return <p>Načítání...</p>;
    }

    if (!data) {
        return <p>Data nebyla nalezena.</p>;
    }

    return (
        <div>
            <Breadcrumbs sport={breadcrumbSport} sportHref={sportHref} league={breadcrumbLeague} leagueHref={leagueHref} />
            <MatchResultPanel data={data} />
            <MatchControlPanel />
        </div>
    );
}
