import React, { useRef, useEffect } from 'react';
import HeaderNavigation from "../headerNavigation";
import Input from "../../components/input";

export default function Home() {
    const inputRef = useRef(null);

    // Nastavení dnešního data při načtení komponenty
    useEffect(() => {
        const today = new Date().toISOString().split("T")[0]; // Získání dnešního data ve formátu YYYY-MM-DD
        if (inputRef.current) {
            inputRef.current.value = today;
        }
    }, []);

    const handleSpanClick = () => {
        if (inputRef.current) {
            inputRef.current.showPicker(); // Otevření date pickeru (moderní prohlížeče)
            inputRef.current.focus(); // Zajištění, že input získá fokus
        }
    };

    return (
        <div id={"home"}>
            <HeaderNavigation />
            <div className="custom-date">
                <Input
                    ref={inputRef}
                    className={"flex justify-end items-end w-fit inputDate"}
                    type={"date"}
                />
                <span className="icon" onClick={handleSpanClick}>
                    <img src={"/images/calendar.svg"} alt={"calendar"} />
                </span>
            </div>
        </div>
    );
}
