import { useState } from 'react'
import { Dialog } from '@headlessui/react'
import { Bars3Icon, XMarkIcon } from '@heroicons/react/24/outline'
import UserLocalStorage from '../Auth/userAuth';
import userAuth from "../Auth/userAuth";

let navigation = [
    { name: 'Domů', href: '/' },
    { name: 'Recepty', href: '/recepty' },
    { name: 'O nás', href: '/onas' },
];

export default function Header() {
    const [mobileMenuOpen, setMobileMenuOpen] = useState(false)

    if(userAuth.isAdmin()){
        navigation = [
            { name: 'Správa receptů', href: '/admin/recepty' },
            { name: 'Správa uživatelů', href: '/admin/uzivatele' },
            { name: 'Recepty', href: '/recepty' },
        ]
    }

    return (
        <header style={headerStyle} className={userAuth.isAdmin() ? "bg-white text-black" : "bg-gray-900"}>
            <nav className={
                `mx-auto flex items-center justify-between p-6 lg:px-8 ${
                    !userAuth.isAdmin() ? "max-w-7xl" : ""
                }`
            } aria-label="Global">
                <div className="flex lg:flex-1 items-center gap-3">
                    <i style={userAuth.isAdmin() ? iconStyleAdmin : iconStyle} className="fa-solid fa-apple-whole fa-rotate-by fa-4xl mb-1"></i>
                    <a href="/" className="-m-1.5 p-1.5 text-base font-semibold leading-6 text-white">
                        <h1 className={userAuth.isAdmin() ? "text-black" : "text-white" }>Zdravé recepty</h1>
                    </a>
                </div>
                <div className="flex lg:hidden">
                    <button
                        type="button"
                        className="-m-2.5 inline-flex items-center justify-center rounded-md p-2.5 text-gray-400"
                        onClick={() => setMobileMenuOpen(true)}
                    >
                        <span className="sr-only">Open main menu</span>
                        <Bars3Icon className="h-6 w-6" aria-hidden="true" />
                    </button>
                </div>
                <div className="hidden lg:flex lg:gap-x-12">
                    {navigation.map((item) => (
                        <a key={item.name} href={item.href} className={userAuth.isAdmin() ? "text-sm font-semibold leading-6 text-black" : "text-sm font-semibold leading-6 text-white"}>
                            {item.name}
                        </a>
                    ))}
                    {UserLocalStorage.isUserLoggedIn() && (
                        <a href="/profil" className={userAuth.isAdmin() ? "text-sm font-semibold leading-6 text-black" : "text-sm font-semibold leading-6 text-white"}>Profil</a>
                    )}

                </div>
                <div className="hidden lg:flex lg:flex-1 lg:justify-end">
                    <a href={UserLocalStorage.isUserLoggedIn() ? "/logout" : "/login"} className={userAuth.isAdmin() ? "text-sm font-semibold leading-6 text-black" : "text-sm font-semibold leading-6 text-white"}>
                        {UserLocalStorage.isUserLoggedIn() ? "Odhlásit se" : "Přihlásit se"} <span aria-hidden="true">&rarr;</span>
                    </a>
                </div>
            </nav>
            <Dialog as="div" className="lg:hidden" open={mobileMenuOpen} onClose={setMobileMenuOpen}>
                <div className="fixed inset-0 z-10" />
                <Dialog.Panel className="fixed inset-y-0 right-0 z-10 w-full overflow-y-auto bg-gray-900 px-6 py-6 sm:max-w-sm sm:ring-1 sm:ring-white/10">
                    <div className="flex items-center justify-between">
                        <a href="/" className="-m-1.5 p-1.5 text-base font-semibold leading-6 text-white">
                            <i style={iconStyle} className="fa-solid fa-apple-whole fa-rotate-by fa-4xl mb-1"></i>
                        </a>
                        <button
                            type="button"
                            className="-m-2.5 rounded-md p-2.5 text-gray-400"
                            onClick={() => setMobileMenuOpen(false)}
                        >
                            <span className="sr-only">Close menu</span>
                            <XMarkIcon className="h-6 w-6" aria-hidden="true" />
                        </button>
                    </div>
                    <div className="mt-6 flow-root">
                        <div className="-my-6 divide-y divide-gray-500/25">
                            <div className="space-y-2 py-6">
                                {navigation.map((item) => (
                                    <a
                                        key={item.name}
                                        href={item.href}
                                        className="-mx-3 block rounded-lg px-3 py-2 text-base font-semibold leading-7 text-white hover:bg-gray-800"
                                    >
                                        {item.name}
                                    </a>
                                ))}
                            </div>
                            <div className="py-6">
                                <a href={UserLocalStorage.isUserLoggedIn() ? "/logout" : "/login"} className={userAuth.isAdmin() ? "text-sm font-semibold leading-6 text-white" : "text-sm font-semibold leading-6 text-white"}>
                                    {UserLocalStorage.isUserLoggedIn() ? "Odhlásit se" : "Přihlásit se"} <span aria-hidden="true">&rarr;</span>
                                </a>
                            </div>
                        </div>
                    </div>
                </Dialog.Panel>
            </Dialog>
        </header>
    )
}

const iconStyle = {
    transform: "rotate(20deg)",
    fontSize: "2rem",
    color: "white"
}

const iconStyleAdmin = {
    transform: "rotate(20deg)",
    fontSize: "2rem",
    color: "black"
}

const headerStyle = {
    boxShadow: "0px 9px 24px -4px rgba(0,0,0,0.75)",
}
