import MatchResult from "./matchResult";
import MatchDetailDate from "./matchDetailDate";
import MatchDetailTeamHome from "./matchDetailTeamHome";
import MatchDetailTeamAway from "./matchDetailTeamAway";

export default function MatchResultPanel(props) {
    console.log(props)
    const favoriteTeamIds = props.favoriteTeams.map(team => team.team_id.id);
    const isHomeFavorited = favoriteTeamIds.includes(props.data.homeTeam.id);
    const isAwayFavorited = favoriteTeamIds.includes(props.data.awayTeam.id);

    const homeTeamPosition = props.positions.teams.map(team => team.id).indexOf(props.data.homeTeam.id) + 1;
    const awayTeamPosition = props.positions.teams.map(team => team.id).indexOf(props.data.awayTeam.id) + 1;
    console.log(homeTeamPosition)

    return(
        <div className={"pb-24 pt-12"} style={styleContainer}>
            <MatchDetailDate date={props.data.date} time={props.data.time} stadium={props.data.homeTeam.stadium}></MatchDetailDate>
            <div className={"lg:px-20 flex flex-row justify-between"}>
                <MatchDetailTeamHome isFavorited={isHomeFavorited} logo={props.data.homeTeam.image} name={props.data.homeTeam.name} id={props.data.homeTeam.id} setFavoriteTeams={props.setFavoriteTeams}></MatchDetailTeamHome>
                <MatchResult status={props.data.status} home={props.data.parametrs.goals_home} away={props.data.parametrs.goals_away}></MatchResult>
                <MatchDetailTeamAway isFavorited={isAwayFavorited} logo={props.data.awayTeam.image} name={props.data.awayTeam.name} id={props.data.awayTeam.id} setFavoriteTeams={props.setFavoriteTeams}></MatchDetailTeamAway>
            </div>
        </div>
    );
}

const styleContainer = {
    backgroundColor: "#241F55",
    fontWeight: "bold",
};
