import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';

// Komponenta pro detekci mobilního zařízení
const MobileAppSuggestion = () => {
    const isMobile = /Mobi|Android/i.test(navigator.userAgent); // Zjišťuje, zda jde o mobilní zařízení

    if (isMobile) {
        return (
            <div style={{ padding: '20px', textAlign: 'center', backgroundColor: '#f0f0f0' }}>
                <p><strong>Stáhněte si mobilní aplikaci pro lepší zážitek!</strong></p>
                <div>
                    <a href="/download-app" style={{ marginRight: '10px', color: 'blue', textDecoration: 'underline' }}>
                        Stáhnout aplikaci
                    </a>
                    <a href="/" style={{ color: 'blue', textDecoration: 'underline' }}>
                        Zůstat na webu
                    </a>
                </div>
            </div>
        );
    }
    return null; // Pokud nejde o mobilní zařízení, nic nezobrazí
};

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
    <React.StrictMode>
        <MobileAppSuggestion />
        <App />
    </React.StrictMode>
);

reportWebVitals();
