import {editFavoriteTeam, getFavoriteTeams} from "../../Repositories/UserRepository";

export default function MatchDetailTeamHome(props) {
    const handleFavoriteClick = async (teamId) => {
        console.log(teamId)
        try {
         await editFavoriteTeam(teamId);
         const updatedFavoriteTeams = await getFavoriteTeams();
            props.setFavoriteTeams(updatedFavorites);
        } catch (error) {
            console.error('Error adding favorite', error);
        }
    };

    return (
        <div className="rounded-lg flex items-center gap-6 w-fit matchDetailTeam matchDetailTeamHome">
            <div className="bg-white p-3 rounded-lg flex flex-col items-center shadow-md absolute">
                <p className="font-bold text-black">FORM</p>
                {["W", "W", "W", "L", "L"].map((result, index) => (
                    <div
                        key={index}
                        className={`w-8 h-8 flex items-center justify-center text-white font-bold text-lg rounded-full mt-2 ${
                            result === "W" ? "bg-green-500" : "bg-red-500"
                        }`}
                    >
                        {result}
                    </div>
                ))}
            </div>

            <div className={"imageClub"}>
                <div className="bg-white p-6 rounded-lg shadow-md flex items-center">
                    <img
                        src={props.logo}
                        alt=""
                        className="h-20 w-20"
                    />
                </div>
                <h2 className={"text-center mt-2"}>{props.name}</h2>
            </div>
            <div className={`text-white flex flex-col items-center cursor-pointer ${props.isFavorited ? "" : "not-favorite"}`} onClick={() => handleFavoriteClick(props.id)}>
                <span className="text-3xl">⭐</span>
                <p className="text-lg font-bold">(1. místo)</p>
            </div>
        </div>
    );
}
