import MatchPanel from "../../components/matchPanel";
import Match from "../../components/match";
import Link from "../../components/link";
import Gap from "../../components/gap";
import InfoPanel from "../../components/InfoPanel";

export default function ContentHome({ games, league, favoriteTeams }) {
    const groupedByLeague = games.reduce((acc, game) => {
        const leagueId = game.league.id;
        if (!acc[leagueId]) {
            acc[leagueId] = [];
        }
        acc[leagueId].push(game);
        return acc;
    }, {});

    const isEmpty = Object.keys(groupedByLeague).length === 0;

    console.log(favoriteTeams)

    return (
        <div className={"lg:px-20 flex flex-row gap-20 contentHome"}>
            <div className={"flex flex-col gap-12"}>
                <InfoPanel className={"infoPanel font-bold"} headline={"Vybrané ligy"}>
                    <Gap gap={"4"}></Gap>
                    {league && league.length > 0 ? (
                        league.slice(0, 10).map(leagueItem => (
                            <Link
                                key={leagueItem.id}
                                classNameDiv={"flex flex-row-reverse items-center justify-end gap-3"}
                                text={leagueItem.name}
                                href={`/league/${leagueItem.id}`}
                            >
                                <img src={leagueItem.image_src} alt={leagueItem.name} className="w-6 h-6 object-contain" />
                            </Link>
                        ))
                    ) : (
                        <></>
                    )}
                </InfoPanel>

                {favoriteTeams.length > 0 && (
                    <InfoPanel className={"infoPanel font-bold"} headline={"Oblíbené týmy"}>
                        {favoriteTeams.map(team => (
                            <Link
                                key={team.team_id.id}
                                classNameDiv={"flex flex-row-reverse items-center justify-end gap-3 mt-4"}
                                text={team.team_id.name}
                                href={`/team/${team.team_id.id}`}
                            >
                                <img src={team.team_id.image_src} alt={team.team_id.name} className="w-6 h-6" />
                            </Link>
                        ))}
                    </InfoPanel>
                )}

            </div>


            <div className={"flex flex-col gap-12 justify-center"}>
                {isEmpty ? (
                    <InfoPanel className={"infoPanel font-bold text-red-700"}
                               headline={"Dnes nejsou naplánovány žádné zápasy."}></InfoPanel>
                ) : (
                    Object.keys(groupedByLeague).map(leagueId => {
                        const leagueGames = groupedByLeague[leagueId];
                        const leagueName = leagueGames[0].league.name;

                        const sortedGames = leagueGames.sort((a, b) => {
                            const timeA = a.time_of_game.split(":").map(num => parseInt(num));
                            const timeB = b.time_of_game.split(":").map(num => parseInt(num));
                            const totalMinutesA = timeA[0] * 60 + timeA[1];
                            const totalMinutesB = timeB[0] * 60 + timeB[1];
                            return totalMinutesA - totalMinutesB;
                        });
                        return (
                            <MatchPanel key={leagueId} headline={leagueName} src={"/images/englad.svg"}>
                                {sortedGames.map(game => (
                                    <Match
                                        key={game.id}
                                        time={game.time_of_game}
                                        team1Img={game.homeTeam.image}
                                        team2Img={game.awayTeam.image}
                                        team1={game.homeTeam.name}
                                        team2={game.awayTeam.name}
                                        score1={game.parametrs.goals_home}
                                        score2={game.parametrs.goals_away}
                                        id={game.id}
                                    />
                                ))}
                            </MatchPanel>
                        );
                    })
                )}
            </div>
        </div>
    );
}
