import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import getTable from "../../Repositories/LeagueRepository";

export default function LeagueDetail() {
    let { id } = useParams();
    let [data, setData] = useState(null);
    let [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchData = async () => {
            setLoading(true);
            try {
                let leagueData = await getTable(id);
                setData(leagueData);
            } catch (error) {
                console.error("Chyba při načítání dat:", error);
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, [id]);

    if (loading) {
        return <div className="loading">Načítání...</div>;
    }

    return (
        <div className="league-detail">
            <h1 className="league-title">{data.league.name} - {data.league.association}</h1>
            <p>{data.league.sport}</p>
            <table className="league-table">
                <h1>Tabulka</h1>
                <thead>
                <tr>
                    <th>Tým</th>
                    <th>Body</th>
                </tr>
                </thead>
                <tbody>
                {data.teams.sort((a, b) => b.points - a.points).map(team => (
                    <tr key={team.id}>
                        <td>{team.name}</td>
                        <td>{team.points}</td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    );
}
