import MatchResult from "./matchResult";
import MatchDetailDate from "./matchDetailDate";
import MatchDetailTeamHome from "./matchDetailTeamHome";
import MatchDetailTeamAway from "./matchDetailTeamAway";

export default function MatchResultPanel(props) {
    const isHomeFavorited = props.favoriteTeams.includes(props.data.homeTeam.id);
    const isAwayFavorited = props.favoriteTeams.includes(props.data.awayTeam.id);

    return(
      <div className={"pb-24 pt-12"} style={styleContainer}>
          <MatchDetailDate date={props.data.date} time={props.data.time} stadium={props.data.homeTeam.stadium}></MatchDetailDate>
          <div className={"lg:px-20 flex flex-row justify-between"}>
              <MatchDetailTeamHome isFavorited={false} logo={props.data.homeTeam.image} name={props.data.homeTeam.name} id={props.data.homeTeam.id}></MatchDetailTeamHome>
              <MatchResult home={props.data.parametrs.goals_home} away={props.data.parametrs.goals_away}></MatchResult>
              <MatchDetailTeamAway isFavorited={true} logo={props.data.awayTeam.image} name={props.data.awayTeam.name} id={props.data.awayTeam.id}></MatchDetailTeamAway>
          </div>
      </div>
    );
}

const styleContainer = {
    backgroundColor: "#241F55",
    fontWeight: "bold",
}
