import Link from "../components/link";

export default function Profile(props) {

    const handleModalClick = (e) => {
        if (e.target === e.currentTarget) {
            props.onClose();
        }
    };

    return (
        <div onClick={handleModalClick} className="modalContainer">
            <div className="modal">
                <div className="modal-header">
                    <div className="icon-container">
                        <img src="/images/profile.svg" alt="login" />
                    </div>
                    <button className="close-btn" onClick={props.onClose}>&times;</button>
                </div>
                <h1>{props.username}</h1>
                <h1>Váš účet u SportMatter</h1>

                <div className={"profileBtnContainer"}>
                    <div>
                        <Link className={"profileBtn"} href={"#"} text={"Změnit přezdívku"} />
                        <Link className={"profileBtn"} href={"#"} text={"Změnit heslo"} />
                        <Link className={"profileBtn"} href={"#"} text={"Upravit moje týmy"} />
                    </div>
                </div>
            </div>
        </div>
    );
}
